namespace BSeller\Api\Handler\Request\Sales;

use BSeller\Api\Handler\Request\HandlerAbstract;
use BSeller\Api\Handler\Response\HandlerInterface;

class PaymentMethodHandler extends HandlerAbstract
{

    /** @var string */
    protected $baseUrlPath = '/api/formas-pagamento';


    /**
     * @return HandlerInterface
     */
    public function paymentMethods()
    {
        /** @var HandlerInterface $responseHandler */
        $responseHandler = $this->service()->get($this->baseUrlPath());
        return $responseHandler;
    }

    /**
     * @return HandlerInterface
     */
    public function cardFlags()
    {
        /** @var HandlerInterface $responseHandler */
        $responseHandler = $this->service()->get($this->baseUrlPath('/cartoes/bandeiras'));
        return $responseHandler;
    }

    /**
     * @return HandlerInterface
     */
    public function paymentConditions()
    {
        /** @var HandlerInterface $responseHandler */
        $responseHandler = $this->service()->get($this->baseUrlPath('/condicoes-pagamento'));
        return $responseHandler;
    }

    /**
     * @return null
     */
    public function entityInterface()
    {
        return null;
    }
}
